// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

contract Auction{
    address public Winner; // The highest bidder's address
    uint public WinnerBid; // The amount of the highest bid

    mapping(address => uint) public ParticipentBalances; // mapping for the amount to return

    constructor() {
        Winner=msg.sender;
        WinnerBid=0;
    }

    /// Bid on the auction with the value sent
    /// together with this transaction.
    /// The value will only be refunded if the
    /// auction is not won.

    function bid() public payable {
    // 1. Check if the bid is greater than the current highest bid
    // 2. Update status variable and the amount to return

        require(msg.value > WinnerBid,"There is already a higher bid.");
        ParticipentBalances[Winner]=WinnerBid;
        Winner=msg.sender;
        WinnerBid=msg.value;
    }

    function withdraw() public {
        address payable msgSender = payable(msg.sender);
        require(canWithdraw(msgSender),"You can't withdraw.");
        require(msgSender.send(ParticipentBalances[msgSender]), "Could not sent");
        ParticipentBalances[msgSender] = 0;
    }

    function canWithdraw(address _addr) public view returns(bool){
        if(ParticipentBalances[_addr] > 0)
            return true;
        return false;
    }

    function getContractBalance() public view returns(uint){
        return address(this).balance;
    }

    function getAccountBalance(address _bidder) public view returns(uint){
        return address(_bidder).balance;
    }

    function isTheHighestBidder()public view returns(bool){
        if(msg.sender == Winner){
            return true;
        }
        return false;
    }


 receive() external payable {}
}
